package com.kdy.oct281t.calc;

// 
public class CalcBean {
	private int x;
	private int y;
	private int result1;
	private int result2;
	private int result3;
	private int result4;
	public CalcBean() {
		// TODO Auto-generated constructor stub
	}

	public CalcBean(int x, int y, int result1, int result2, int result3, int result4) {
		super();
		this.x = x;
		this.y = y;
		this.result1 = result1;
		this.result2 = result2;
		this.result3 = result3;
		this.result4 = result4;
	}

	public int getX() {
		return x;
	}

	public void setX(int x) {
		this.x = x;
	}

	public int getY() {
		return y;
	}

	public void setY(int y) {
		this.y = y;
	}

	public int getResult1() {
		return result1;
	}

	public void setResult1(int result1) {
		this.result1 = result1;
	}

	public int getResult2() {
		return result2;
	}

	public void setResult2(int result2) {
		this.result2 = result2;
	}

	public int getResult3() {
		return result3;
	}

	public void setResult3(int result3) {
		this.result3 = result3;
	}

	public int getResult4() {
		return result4;
	}

	public void setResult4(int result4) {
		this.result4 = result4;
	}
}
